# Création d'une étendue DHCP# Demande à l'utilisateur le nom de l'étendue DHCP$NomScope = Read-Host = "Nom de l'étendue DHCP ?"# Demande à l'utilisateur le début de l'étendue$StartScope = Read-host = "Adresse de début d'étendue"# Demande à l'utilisateur le début de l'étendue$EndScope = Read-host = "Adresse de fin d'étendue"# Demande à l'utilisateur le masque de sous réseau$Mask = Read-host = "Masque de sous réseau ?"Add-DhcpServerv4Scope -Name $NomScope -StartRange $StartScope -EndRange $EndScope -SubnetMask $Mask$addressGateway = Read-Host = "Adresse passerelle par défaut ?"$addressDNS = Read-Host = "Adresse serveur DNS ?"$addressNetScope = Read-Host = "Adresse de l'etendue ?"Set-DhcpServerV4OptionValue -DnsServer $addressDNS -Router $addressGateway -ScopeId $addressNetScope